class FruitInfo {
  final String name;
  final String frenchTranslation;
  final String arabicTranslation;
  final String definition;

  FruitInfo({
    required this.name,
    required this.frenchTranslation,
    required this.arabicTranslation,
    required this.definition,
  });
}

class FruitData {
static List<FruitInfo> fruits = [
  FruitInfo(
      definition:
          "موضوع علم الآخرة هو البحث في المسائل المتعلقة بنهاية العالم، ومصير الإنسان، من موت، وبعث، وحساب، وجنة، ونار. ومع أن اصطلاح علم الآخرة اصطلاح لاهوتي يطلق على البحث في نهاية العالم، ويوم الحساب، وما يتبعه من الاستقرار المسعد او المشقي، فإن الفلاسفة لا يجتنبون استعماله، مثال ذلك قولهم: الإيشاتولوجيا الكونية، والإيشاتولوجيا الأخلاقية. ويطلق اصطلاح علم الآخرة أيضا على النظريات التي تبحث في مصير الإنسانية بعد اجتيازها مرحلة الوجود الفعلي، أو على النظريات التي تبحث في الحد النهائي الشرطي لوجود إنساني ليس بعده تاريخ. وعلم الآخرة مرادف لعلم المعاد.",
      arabicTranslation: "Eschatology",
      frenchTranslation: "Eschatologie",
      name: "الاخرة (علم)"),
  FruitInfo(
      definition:
          "الآلة شيء مركب من أجزاء محكمة الترتيب، تسمح بنقل الحركة أو بصنع بعض الأشياء. والآلي هو المنسوب إلى الآلة، أي ما ينتج منها، كالتطريز الآلي، أو يتم بها، كالحساب الآلي، أو يتحرك معها، كالسلّم الآلي. وقد يطلق الآلي على الرجل الذي يعمل كالآلة دون روية وفكر.\nوالآلية “ Mécanisme” مذهب فلسفي يقرر أن بعض الظواهر الطبيعية، أو كلها، تنحلّ إلى جملة من العوامل الميكانيكية، وهو مرادف للمذهب المادي. ويطلق لفظ الآلية مجازا على كل عملية يمكن أن يكون فيها جملة من المراحل المتعاقبة المتعلقة بعضها ببعض، تقول: آلية الانتباه، وآلية الذاكرة، وآلية القياس. أو يطلق على جملة من الإجراءات الضرورية لإنجاز بعض الأعمال الإدارية، تقول: آلية الانتخابات، وآلية وضع الموازنة.\nوالآلية مضادة للديناميكية والغائية، والحيوية. أما التضاد بينها وبين الديناميكية، فيرجع إلى أنها تريد أن تفسر ظواهر العالم المادي بحركة أجزاء المادة، دون افتراض أي طاقة فيها. وأما التضاد بينها وبين الغائية فيرجع إلى أنها تريد أن تفسر جميع الظواهر الطبيعية بالأسباب الفاعلة، بصرف النظر عن الأسباب الغائية، وأما التضاد بينها وبين الحيوية فيرجع إلى أنها تريد أن تفسر جميع ظواهر الحياة بخواص المادة “الفيزيائية والكيميائية”، دون اللجوء إلى مبدأ آخر.",
      arabicTranslation: "Mechanism",
      frenchTranslation: "Mécanisme",
      name: "الالية"),
  FruitInfo(
      definition:
          "الآن في اللغة الوقت، قيل: أصله أو آن، حذفت الألف الأولى، وقلبت الواو ألفا، فصار آنا. وهو عند الفلاسفة نهاية الماضي، وبداية المستقبل، به ينفصل أحدهما عن الآخر. فهو فاصل بينهما بهذا الاعتبار، وواصل بينهما باعتبار أنه حدّ مشترك، أو طرف موهوم، بين زمانين متعاقبين. فنسبته إلى الزمان كنسبة النقطة إلى الخط الغير المتناهي، أو كنسبة الوحدة إلى العدد. فكما أنه لا نقطة في الخطّ الّا بالفرض، كذلك لا آن في الزمان إلا بالفرض. والفرق بين الوحدة والآن أن الوحدة جزء من العدد، في حين أن الآن حدّ الزمانين الماضي والمستقبل، أو نهاية الزمان، ونهاية الشيء خارجة عنه. والآنات الزمانية لا تعتبر متعاقبة إلا إذا فرضت خارجة بعضها عن بعض. وقد قيل: الآن أمر لا ينقسم، وهو يفعل بسيلانه الزمان. والآن الدائم هو امتداد الحضرة الإلهية الذي يندرج به الأزل في الأبد، وكلاهما في الوقت الحاضر، فيتّحد به الأزل والأبد والوقت الحاضر معا. فلذلك يقال له باطن الزمان، وأصل الزمان، والسر مد، لأن الآنات الزمانية نقوش وتغيرات يظهر بها صوره، وهو ثابت على حاله سرمدا.\nوقد يقال آن لزمان صغير المقدار عند الوهم، كالذي عن جنبتي الآن الحقيقي. وهو زمان متوسط بين الماضي والمستقبل، يدركه العقل من حيث هو كل. وبالجملة، فالآن قد يطلق على طرف الزمان. وقد يطلق على الزمان القصير. وعند السالكين هو العشق.\n “ر: زمان، وقت، لحظة”",
      arabicTranslation: "Instant ,moment",
      frenchTranslation: "Instant",
      name: "الان"),
  FruitInfo(
      definition:
          "الأبد في اللغة الدهر، والدائم، والقديم، والأزلي، والجمع آباد، وأبود. وهو، في الاصطلاح، الزمان الذي ليس له ابتداء ولا انتهاء، أو المدة التي لا يتوهم انتهاؤها بالفكر والتأمل، أو الشيء الذي لا نهاية له. والفلاسفة يفرقون بين الأبد الزماني والأبد اللازماني. فالأبد الزماني هو المدة التي ليس لها حد محدود في الماضي والمستقبل، أو الزمان الدائم الذي ليس له ابتداء ولا انتهاء. وهو بهذا المعنى صفة من صفات اللّه، لأنه تعالى كان، وسيكون دائما. أما العالم الحادث الفاني فليس أبديا، لأنه لم يكن، ولن يكون دائما. وفلاسفة القرون الوسطى يقسمون الأبد الزماني قسمين، فيسمون دوام الوجود في الماضي أزلا “ A parte ante”، ودوام الوجود في المستقبل أبدا “ A parte poste”. ولا فرق بين الأزل والأبد بالنسبة إلى اللّه تعالى، لأن أبده عين أزله، وأزله عين أبده، بل الأزل والأبد بالنسبة إليه صفتان أظهرتهما الإضافة الزمانية لتعقل وجوب وجوده، وإلا فلا أزل، ولا أبد، كان اللّه ولم يكن شيء معه. أما الأبد اللازماني فهو المطلق، أو الشيء الذي لا نهاية له. وهو مقابل للزمان. فكل حادث، وكل موجود متناه هما في الزمان. أما الموجود الأبدي فليس حادثا، وليس له قبل ولا بعد، بل هو الحاضر الأبدي “ Duratio tota simul”، وهو فوق الزمان. \nلقد كان الفلاسفة “الإيليون” مثلا يفرقون بين الوجود والكون، فيقولون: أن المطلق لا يوصف إلا بالوجود، وأن الأشياء المتناهية لا توصف إلا بالكون، وأنه ليس للوجود ماض ولا مستقبل، ولكنه في حاضر لا يزول. فأخذ أفلاطون وأرسطو عنهم هذا الأصل وقالا إن الموجود الكامل لا يتكون، ولا يتغير، وهو واحد أبدي لا حركة ولا تغيّر في وجوده التام غير المنقسم، ولا صلة له بالزمان. أما الموجودات غير الكاملة، فتولد وتتغير وتتكون دون انقطاع، وهي في الزمان. وعلى ذلك فالفرق بين الأبد والزمان ليس بالرتبة والمقدار، كالفرق الذي بين العدد الغير المتناهي والعدد المتناهي، وإنما هو بالطبع، لأن أحدهما غير منقسم، والآخر منقسم إلى غير نهاية، وليس بينهما مقياس مشترك. وعلى ذلك أيضا يمكن أن يوصف العالم والزمان بأنهما لا ابتداء لهما ولا انتهاء، ولا يكونان مع ذلك أبديين، لأنه يكفي أن يكون وجودهما مشتملا على التبدل والتغير حتى يكون غير أبدي. هذا الذي أشار إليه أفلاطون بقوله: ان الزمان صورة متحركة للابدية غير المتحركة، وهذا أيضا ما ذهب إليه أرسطو عند استدلاله على وجود اللّه بوجود الحركة والتغير، فخلص من ذلك إلى القول بوجود محرك لا يتحرك. إن هذا الأبد اللازماني هو المعنى الذي أخذ به أيضا القديس توما الاكويني، وديكارت، ومالبرانش، وبوسويه، وفنلون، وليبنيز، وكانت. والأبد والأمد متقاربان. لكن الأبد لا يتقيد، فلا يقال أبد كذا، والأمد ينحصر، فيقال أمد كذا، كما يقال زمان كذا. وأبدا ظرف زمان للتأكيد في المستقبل نفيا وإثباتا، فصار كقط والبتة في تأكيد الزمان الماضي، يقال. ما فعلت كذا قط والبتة، ولا أفعله أبدا، أو أفعله أبدا. ويقال أيضا لا آتية أبد الآبدين، ودهر الداهرين، وآخر الأبد كناية عن المبالغة في التأييد.",
      arabicTranslation: "Eternity",
      frenchTranslation: "Eternité",
      name: "الابد"),
  FruitInfo(
      definition:
          "الإبداع في اللغة إحداث شيء على غير مثال سابق. وعند البلغاء: اشتمال الكلام على عدة ضروب من البديع. وله في اصطلاح الفلاسفة عدة معان. الأول: تأسيس الشيء عن الشيء، أي تأليف شيء جديد من عناصر موجودة سابقا كالإبداع الفنّي، والإبداع العلمي، ومنه التخيل المبدع في علم النفس. والثاني: إيجاد الشيء من لا شيء كإبداع الباري سبحانه، فهو ليس بتركيب ولا تأليف، وإنما هو إخراج من العدم إلى الوجود. وفرقوا بين الإبداع والخلق، فقالوا: الإبداع إيجاد شيء من لا شيء، والخلق إيجاد شيء من شيء كذلك قال اللّه تعالى: بديع السماوات والأرض، ولم يقل بديع الإنسان، بل قال خلق الإنسان، فالإبداع بهذا المعنى أعم من الخلق. والثالث: إيجاد شيء غير مسبوق بالعدم، ويقابله الصنع، وهو إيجاد شيء مسبوق بالعدم. قال “ابن سينا” في الإشارات: «الإبداع هو أن يكون من الشيء وجود لغيره متعلق به فقط، دون متوسط من مادة أو آلة أو زمان. وما يتقدمه عدم زماني لم يستغن عن متوسط» “الإشارات، النمط الخامس، ص 153 من طبعة فورجت”. وهذا تنبيه إلى أن كل مسبوق بعدم فهو مسبوق بمادة وزمان. والغرض منه، كما قال “الطوسي”، عكس نقيضه، وهو أن كل ما لم يكن مسبوقا بمادة وزمان لم يكن مسبوقا بعدم. فالإبداع هو إذن أن يكون من الشيء وجود لغيره من دون أن يكون مسبوقا بمادة ولا زمان. كالعقل الأول في فلسفة “ابن سينا” فهو يصدر عن واجب الوجود من دون أن يكون صدوره عنه متعلقا بمادة وزمان. والإبداع بهذا المعنى أعلى رتبة من التكوين والإحداث، فإنّ التكوين هو أن يكون من الشيء وجود مادي، والإحداث أن يكون من الشيء وجود زماني. وكل واحد منهما يقابل الإبداع. فالتكوين يقابله لكونه مسبوقا بالمادة، والإحداث يقابله أيضا لكونه مسبوقا بالزمان. والإبداع أقدم منهما، لأن المادة لا يمكن أن تحصل بالتكوين، والزمان لا يمكن أن يحصل بالإحداث. إذن التكوين والإحداث مترتبان على الإبداع، وهو أقرب منهما الى اللّه. والرابع: الإبداع الدائم “ Création Continuée” وهو عند الفلاسفة الأصوليين والديكارتيين الفعل الذي يبقي به اللّه العالم. وهو عين الفعل الذي يخرجه به من العدم الى الوجود. فالله اذن مبدع ومبق، لأنه إذا قبض جوده بطلت الموجودات كلها دفعة واحدة، وهذا أيضا يقابل التأليف، لأن التأليف باق، وان أمسك المؤلف تأليفه، أما الابداع فهو ايجاد وابقاء. والفلاسفة الذين يقولون بوحدة الوجود لا يحتاجون الى القول بابداع العالم، ولكن الذين يجعلون اللّه متميزا عن العالم يقولون: أن علاقة أحدهما بالآخر لا تعدو ثلاثة أحوال. فإما أن يقال: إن العالم قديم، وان اللّه عالم بالكل وبالواجب أن يكون عليه الكل حتى يكون على أحسن نظام. وهذا مذهب القائلين بالعناية الالهية كابن سينا وغيره. وإما أن يقال: ان لقدرة اللّه تأثيرا في مبدأ العالم، من حيث أنها تنظم المادة الموجودة سابقا، وترتبها كما يرتب الصانع صنعه. وإما أن يقال: ان لها تأثيرا في إخراج العالم، من العدم الى الوجود، وهذا مذهب القائلين بالإبداع. أعني القول: إن اللّه ليس مؤلف نظام الأشياء، ومرتب صورها فحسب، وإنما هو مبدع مادتها أيضا. ومعنى ذلك أن كل ما لم يكن موجودا، فقد صار بفعل قدرته تعالى موجودا.",
      arabicTranslation: "Creation",
      frenchTranslation: "Création",
      name: "الابداع"),
  FruitInfo(
      definition:
          "الإبستمولوجيا لفظ مركب من لفظين: أحدهما ابيستما “ Epistemé” وهو العلم، والآخر لوغوس “ Logos” وهو النظرية أو الدراسة. فمعنى الابستمولوجيا اذن نظرية العلوم، أو فلسفة العلوم، أعني دراسة مبادئ العلوم، وفرضياتها، ونتائجها، دراسة انتقادية توصل الى ابراز أصلها المنطقي، وقيمتها الموضوعية.\nفالابستمولوجيا تختلف إذن عن دراسة طرق العلوم من جهة، وعن دراسة تركيب القوانين العلمية من جهة ثانية. لأنّ الدراسة الأولى قسم من المنطق التطبيقي، والثانية قسم من الفلسفة الوضعية، أو فلسفة التطور.\nونحن نفرق بين الابستمولوجيا ونظرية المعرفة Théorie de la Connaissance وإن كانت الأولى مدخلا ضروريا للثانية. ذلك لأن الابستمولوجيا لا تبحث في المعرفة من جهة ما هي مبنية على وحدة الفكر، كما في نظرية المعرفة، بل تبحث فيها من جهة ما هي معرفة بعديّة مفصلة على أبعاد العلوم، وأبعاد موضوعاتها. ومع ذلك فإنّ اصطلاح الابستمولوجيا في الانكليزية مرادف لاصطلاح نظرية المعرفة، أما في اللغة الفرنسية، فهو مختلف عنه، لأن معظم الفلاسفة الفرنسيين لا يطلقونه الأعلى فلسفة العلوم وتاريخها الفلسفي. وإذا كان بعضهم يوسع معناه ويطلقه على سيكولوجية العلوم، فمرد ذلك إلى أن دراسة تطور العلوم، لا تنفصل عن نقدها المنطقي، ولا عن مضمونها الحسّي المشخص. \n“ر: فلسفة العلوم، ونظرية المعرفة”.",
      arabicTranslation: "Epistemology",
      frenchTranslation: "Epistémologie",
      name: "الابستمولوجيا"),
  FruitInfo(
      definition:
          "الأبيقوري هو المنسوب إلى أبيقور، ويطلق على انصار مذهبه، أو على ما يتعلق بهذا المذهب. أما في اللغة الجارية فإن الأبيقوري هو الرجل الذي يحب التمتع باللذات، والخيرات، من يسار، ورفاهة، ومأكول، ومشروب، وملبوس، ويكون على العموم حاذقا في اختيار لذاته، دقيقا في معرفة قيمتها. وفي هذا الاستعمال الشائع التباس، لأنه لا يميز بين نظرية أبيقور الداعية إلى القناعة، والاعتدال، والزهد والاستمتاع باللذات المعنوية، وبين الأبيقوريين الحقيقيين كلوكريس وغيره.\nوالأبيقورية “ Epicurisme” مذهب أبيقور القائم على إسعاد الذات بلذة معنوية لا يعقبها ألم، وتطلق أيضا على الصفات التي يتصف بها أنصار هذا المذهب.",
      arabicTranslation: "Epicurean",
      frenchTranslation: "Epicurien",
      name: "الابيقوري"),
  FruitInfo(
      definition:
          "الاتحاد في الأصل هو صيرورة الشيئين المختلفين شيئا واحدا. وله عدة درجات: أدناها درجة الاشتراك البسيط في أمور عرضية، وأعلاها درجة الاتحاد الصوفي.\nوليس المقصود بالاتحاد أن يصير الشيء شيئا آخر، ولا أن يزول أحد الشيئين ويبقى الآخر، وإنما المقصود به أن يكون بين الشيئين علاقة يشتركان فيها مع احتفاظ كل منهما بهويته. مثال ذلك: الاتحاد بطريق التركيب، وهو أن ينضمّ شيء إلى آخر، فيحصل منهما شيء ثالث. لذلك قال ابن سينا: «الاتحاد هو حصول جسم واحد بالعدد من اجتماع اجسام كثيرة» “رسالة الحدود”.\nوكل اتحاد يوجب بقاء الذوات الداخلة فيه متميزة الوجود بعضها عن بعض، كاتحاد النفس بالبدن، فهو اتحاد جوهري “ Union substantielle” لا يمنع عقولنا من تصور حدوده تصورا واضحا ومتميزا.\nوقد يطلق الاتحاد على اشتراك الأشياء في محمول واحد ذاتي، أو عرضي، أو على اشتراك المحمولات في موضوع واحد “كالطعم والرائحة في التفاحة”، أو على اجتماع المحمول والموضوع في ذات واحدة، أو على اجتماع اجسام كثيرة: إما بالبنيان كالمدينة، وإما بالتماس كالكرسي والسرير، وإما بالاتصال كأعضاء الحيوان.\nوقد يطلق الاتحاد أيضا على جملة أفراد يجمعهم هدف واحد كاتحاد الكتاب، واتحاد الطلاب. والاتحاد مرادف للاتفاق، ويقابله الافتراق. والاتحاد في الجنس يسمى مجانسة، وفي النوع مماثلة، وفي الخاصة مشاكلة، وفي الكيف مشابهة، وفي الكم مساواة، وفي الاطراف مطابقة، وفي الاضافة مناسبة، وفي جميع هذه المعاني موازاة.\nوالاتحاد عند الصوفية هو شهود وجود واحد مطلق من حيث أن جميع الأشياء موجودة بوجود ذلك الواحد، معدومة في أنفسها. “ر:الواحد، الوحدة”.",
      arabicTranslation: "Union",
      frenchTranslation: "Union",
      name: "الاتحاد"),
  FruitInfo(
      definition:
          "اتفق الرجلان على الشيء، وفيه: تقاربا واتحدا. واتفق معه وافقه، واتفق الأمر: وقع عرضا. والاتفاق هو اشتراك الأفراد في الآراء أو الميول أو الأهداف أو الأعمال الخ. او اشتراك دولتين أو أكثر في ميثاق يتعلّق ببعض الشؤون السياسية او الاقتصادية او الثقافية.\nوالاتفاقية في المنطق الصوري هي التي يحكم فيها بصدق التالي “ Consequent” على تقدير صدق المقدم “ Antecedent”، لا لعلاقة بينهما موجبة لذلك، بل لمجرد صدقهما، كقولنا: ان كان الانسان ناطقا فالحمار ناهق. وقد يقال إنها هي التي يحكم فيها بصدق التالي فقط، ويجوز أن يكون المقدم فيها صادقا أو كاذبا، وتسمّى بهذا المعنى اتفاقية عامة، والمعنى الأول اتفاقية خاصة للعموم والخصوص بينهما، فإنّه متى صدق المقدم صدق التالي، ولا ينعكس “تعريفات الجرجاني” ويطلق “هنري بوانكاريه” لفظ الاتفاقي “ Conventionnel” على المسلمات الهندسية، لأن هذه المسلمات ليست مبادئ قبيلة، بديهية بذاتها، ولا حقائق يتوصل إليها بتعميم نتائج التجربة، ولا فرضيات قابلة للتحقيق الدقيق، وإنما هي اصطلاحات موافقة “ Commode” يسلم بها العقل لمطابقتها للأشياء الخارجية.\nوطريقة الاتفاق Methode de Concordance في المنطق التطبيقي هي طريقة التلازم في الوقوع “ر: لفظ الطريقة”، وتلخّص في أنه إذا اشتركت حالتان أو أكثر لظاهرة ما في ظرف واحد فإنّ هذا الظرف يكون علة أو معلولا لهذه الظاهرة. ويجيء الاتفاق بمعنى المصادفة. “ Hasard “",
      arabicTranslation: "Agreement Accord ,Convention ،",
      frenchTranslation: "Concordance Accord ,Convention ،",
      name: "الاتفاق"),
  FruitInfo(
      definition:
          "الاثنوغرافيا علم اجتماعي يصف أحوال الشعوب، ويدرس أنماط حياتهم، ومختلف المظاهر المادية لنشاطهم في مؤسساتهم، وتقاليدهم، وعاداتهم، كالمأكل، والمشرب، والملبس، وغيرها.",
      arabicTranslation: "Ethnography",
      frenchTranslation: "Ethnographie",
      name: "الاتنوغرافيا"),
  FruitInfo(
      definition:
          "الإثنولوجيا علم اجتماعي يفسّر الظواهر التي يصفها علم الاثنوغرافيا، ويدرسها دراسة نظرية تسمح بتصنيفها وتعليلها. وقد يطلق اسم الاتنولوجيا في الانكليزية والالمانية على علم الانسان “ Anthropologie”.",
      arabicTranslation: "Ethnology",
      frenchTranslation: "Ethnologique",
      name: "الاتنولوجيا"),
  FruitInfo(
      definition:
          "الأثر نتيجة الشيء، وله عدة معان:\nالأول بمعنى النتيجة، وهو الحاصل من الشيء.\nوالثاني بمعنى العلامة، وهو السمة الدالة على الشيء.\nوالثالث بمعنى الخبر، ويطلق على كلام السلف، لا على فعلهم.\nوالرابع ما يترتب على الشيء، وهو المسمى بالحكم عند الفقهاء “ر: تعريفات الجرجاني وكشاف اصطلاحات الفنون للتهانوي”.\nوالآثار جمع أثر، وهي اللوازم المعللة بالشيء. وقد يطلق الأثر على الشيء المتحقق بالفعل، باعتباره حادثا عن غيره، وهو، بمعنى ما، مرادف للمعلول أو للمسبّب عن الشيء “ر: لفظ المعلول”.\nوقانون الأثر عند “ثورنديك” « Loi de l'effet» هو القول إن النجاح في العمل يدفع إلى تكراره، والإخفاق فيه يدفع إلى اجتنابه.",
      arabicTranslation: "Effect",
      frenchTranslation: "Effet",
      name: "الاثر"),
  FruitInfo(
      definition:
          "الاجتماع ضد الافتراق. قال ابن سينا: «الاجتماع هو وجود أشياء كثيرة يعمّها معنى واحد، والافتراق مقابله» “رسالة الحدود”. وقد أطلق ابن خلدون اسم الاجتماع الإنساني على عمران العالم، قال: «إن الاجتماع الإنساني ضروريّ، ويعبر الحكماء عن هذا بقولهم إن الإنسان مدني بالطبع» “المقدمة، ص 69 من طبعة دار الكتاب اللبناني، بيروت 1967”.\nويعدّ ابن خلدون أوّل السابقين الى تأسيس علم الاجتماع، لأنه حدّد موضوع هذا العلم وسمّاه بعلم العمران، ولأنه قال بخضوع الظواهر الاجتماعية لقانون السببية، ومع أن “مونتسكيو” و “كوندورسيه” قد نسجا على منوال ابن خلدون في تعليل ظواهر الحياة الاجتماعية بأسباب طبيعية، فإن أول فيلسوف أو ربي استعمل اصطلاح علم الاجتماع “ sociologie”، وأطلقه على البحث في الظواهر الاجتماعية، هو الفيلسوف الوضعي “أوغست كومت”. قال: «أعتقد أنه يجب علي أن أخاطر بنفسي منذ الآن في استعمال هذا الاصطلاح الجديد بدلا من اصطلاح الفيزياء الاجتماعية الذي استعملته سابقا، وذلك للدلالة باسم واحد على ذلك القسم الإضافي من الفلسفة الطبيعية المتعلّق بدراسة القوانين الخاصة بالظواهر الاجتماعية»  A .Comte ,Cours de philosophie positive ,47 lec .1839, . وينقسم هذا العلم عنده إلى قسمين: أو لهما السكون او التوازن الاجتماعي “ statique Sociale”، وثانيهما الحراك الاجتماعي. “ Sociale Dynamique”.\nومعنى ذلك كله أن علم الاجتماع يبحث في الظواهر الاجتماعية من جهة ما هي خاضعة لقوانين طبيعية كغيرها من الظواهر المادية أو الحيوية.\nومفهوم علم الاجتماع يتضمن القول: ان للجماعات الإنسانية طبائع خاصة لا تنحلّ الى الطبائع التي يبحث فيها علم النفس أو علم الحياة.\nوالمذهب الاجتماعي “ Sociologisme” هو المذهب الذي يفسّر المسائل الفلسفية الأساسية، وحوادث تاريخ الأديان، بعلم الاجتماع. وهو ضد المذهب النفسي “ Psychologisme” الذي يفسّر الظواهر الاجتماعية بالظواهر النفسية.\nقال “بوترو” «إن المذهب النفسي والمذهب الاجتماعي يرجعان الظواهر الدينية الى الظواهر الطبيعية للفاعلية النفسية أو الاجتماعية» science et religion, Boutroux” P.342 ”، وهو يجعل هذين المذهبين مقابلين للمذهب الروحي، أو لمذهب العمل، أو لمذهب التجربة الدينية.\nوالمذهب الاجتماعي في علم الجمال “ sociologisme esthetique” هو المذهب الذي يفسر الشعور بالجمال بأسباب اجتماعية أو الذي يجعل غاية الفن احداث انفعال جمالي ذي صفة اجتماعية.\nوالمذهب الاجتماعي في علم الأخلاق “ Sociologisme morale” هو المذهب الذي يرجع شعور الفرد بالإلزام الاخلاقي الى متطلبات الحياة الاجتماعية ومقتضياتها.\nوقد أطلق “أوغست كومت” لفظ عبادة المجتمع “ sociolatrie” على ما في الحياة الاجتماعية من روابط مختلفة المراتب تحمل كل فرد على الإسهام في الاحتفالات المشتركة التي يقيمها المجتمع.\nوأطلق ايضا اصطلاح الحكم الجماعي “ sociocratie” على الحكم الذي يعهد في السلطة الى الجماعة من جهة ما هي كل عضوي.\nويطلق اصطلاح المركزية الاجتماعية “ Sociocentrisme” على اعتقاد المرء أن المجتمع الذي يعيش فيه مركز العالم. والاجتماعي هو المنسوب إلى الاجتماع تقول: العالم الاجتماعي، والطريقة الاجتماعية. والاجتماعية “ socialite” هي العلاقات الاجتماعية “ Sociales Relations”، أو مجموع الصفات التي يتميز بها الشيء الاجتماعي. وطريقة القياس الاجتماعي، “ Sociometrie” تقوم على تطبيق القياس في علم الاجتماع. ويتم هذا القياس بوضع روائز Tests واستبيانات Questionnaire يجيب فيها كل فرد عن رأيه، ثم تحصى أجوبة الأفراد، وتبين نسبتها العددية إلى المجموع. وطريقة القياس هذه مصحوبة بطريقة رسم الأشكال البيانية “ sociogramme” التي تمثل علاقات الأفراد بعضهم ببعض.",
      arabicTranslation: "Sociology",
      frenchTranslation: "Sociologie",
      name: "الاجتماع (علم)"),
  FruitInfo(
      definition:
          "الإجماع في اللغة هو العزم، والاتفاق. وله في الاصطلاح القديم معنيان: أحدهما عزم أهل الحل والعقد على أمر معين، والآخر اتفاق المجتهدين في عصر على أمر ديني. وهو أحد الحجج الشرعية.\nويطلق الإجماع في اصطلاحنا على اتفاق أفراد طائفة من الطوائف في العواطف والآراء، تقول: اجمع رأيهم على كذا أي اتفقوا عليه، ومنه قولهم: وافق المجلس على مشروع القانون بالاجماع. ومتى أجمع الأفراد على أمر اصبحوا بالقياس إلى ذلك الأمر روحا واحدة.\nوالاجماعية “ Unanimisme” مذهب أدبي مضاد للمذهب الفردي، وهو يوجب على الكاتب المسرحي أو الروائي أن يعبر عن عواطف الجماهير وآرائهم، لا عن عواطف فئة معينة من الناس.",
      arabicTranslation: "Unanimity ,Consensus",
      frenchTranslation: "Unanimité, Consensus",
      name: "الاجماع"),
  FruitInfo(
      definition:
          "أحبط فلان عمل فلان أبطله، وجعله يخفق، ويذهب سدى، ويرادفه الخيبة والإخفاق. ويقوم الاحباط على حرمان المرء التمتع بنتائج عمله، أو على صده عما يؤمل الحصول عليه، أو يتوقعه. وقد انتشر استعمال هذا اللفظ في علم النفس وعلم الاجتماع، حتى أطلق على كل توتّر عاطفي ناشئ عن هذا الصد.",
      arabicTranslation: "frustration",
      frenchTranslation: "Frustration",
      name: "الاحباط"),
  FruitInfo(
      definition:
          "احترم الشخص هابه. والاحترام شعور خاص يتضمّن الاعتراف بما لبعض الأشخاص أو المثل العليا من قيمة أخلاقية. وفي كتاب نقد العقل العملي لكانت تحليل لهذا الشعور من جهة ما هو أحد بواعث العقل العملي.\nومن معاني الاحترام: الامتناع عن التفريط فيما يجب القيام به من حق القانون، أو الشخص، أو الشيء، تقول: احترام الشخص الإنساني، واحترام الحريات، واحترام الحقيقة، واحترام الحقوق المكتسبة.\nقال “كانت”: ان الاحترام دين لا بدّ من تأديته الى من يستحقه، والقانون الأخلاقي مقدس، ومع أن الإنسان، من حيث هو كائن طبيعي، بعيد عن التقديس، الّا أن الانسانية الممثلة في شخصه يجب أن تكون مقدسة.\nونسبة الاحترام الى الحبّ كنسبة الاحتقار إلى الكره. وإذا كان من حق الاحترام أن يكون مصحوبا بقسط من الحب فإنّ من شقاء المحبين أن يحبوا أشخاصا لا يستحقون الاحترام.",
      arabicTranslation: "Respect",
      frenchTranslation: "Respect",
      name: "الاحترام"),
  FruitInfo(
      definition:
          "قياس الإحراج حجة تكون إحدى مقدماتها قضية عنادية ذات احتمالين، وتكون مقدماتها الأخرى دالة على أن كل احتمال من هذين الاحتمالين يتضمن النتيجة نفسها. وهو قياس مزدوج، أو قياس ذو حدين يحرج الخصم ويلزمه بقبول النتيجة.\nوالقضية العنادية أو التبادلية “ Alternative” في قياس الاحراج اما ان تكون حملية، وإما أن تكون شرطية.\nفإذا كانت حملية مطلقة وضع قياس الاحراج على الشكل التالي:\nتقول للخصم: لا بدّ من الاختيار بين “ب” و “ج”، لأن الحق لا يعدو هما فإمّا أن يكون الصادق “ب”، وإما أن يكون “ج”.\nعلى أنه إذا كان “ب” صادقا، كان “ق” صادقا.\nوإذا كان “ج” صادقا، كان “ق” صادقا أيضا.\nف “ق” صادق إذن بالضرورة.\nوإذا كانت المقدمة الأولى والنتيجة قضيتين شرطيتين كان قياس الاحراج كما يلي:\nإن كان “ب” صادقا، كان “ج”، أو “د” صادقا.\nوإن كان “ج” صادقا، كان “ق” صادقا.\nوإن كان “د” صادقا، كان “ق” صادقا أيضا.\nوإذن: إن كان “ب” صادقا كان “ق” صادقا.\nوقد يطلق قياس الاحراج على الاستدلال الذي تكون فيه القضية التبادلية مشتملة على أكثر من احتمالين.\nأو يطلق على الاستدلال الذي يكون فيه التقابل بين قضيتين متناقضتين، لأن احداهما اذا كانت صادقة، كانت الأخرى كاذبة، والعكس بالعكس.\nوقد أطلق “رينوفيه” لفظ الاحراج على التقابل بين رأيين فلسفيين، بحيث يلزم عن إثبات أحدهما إنكار الآخر، وعن إنكاره إثبات الآخر.\nومن شرط الاحراج الدقيق أن يسلّم الخصم بأن القضية لا تتضمن الّا احتمالين، لأنه إذا لم يسلم بذلك وكان لديه احتمال ثالث لم يصحّ الاحراج.\nوأوضح أشكال الاحراج ان تجعل القضية التبادلية أو العنادية مشتملة على حدين متناقضين، بحيث يؤدي إثبات أحدهما إلى إبطال الآخر، مثال ذلك: قول أرسطو: إما أن يكون التفلسف واجبا، وإما أن لا يكون واجبا، أو قولنا إما أن يسمح العلم بالتنبؤ واما ان لا يسمح بالتنبؤ، فاذا لم يسمح بالتنبؤ لم يكن له قيمة عملية، واذا سمح بالتنبؤ كان له\nقيمة محققة من جهة ما هو وسيلة من وسائل التأثير في الطبيعة.",
      arabicTranslation: "Dilemma",
      frenchTranslation: "Dilemme",
      name: "الاحراج (قياس)"),
  FruitInfo(
      definition:
          "الإحساس ظاهرة نفسية متولدة عن تأثر احدى الحواس بمؤثر ما.\nوله معان مختلفة تابعة لتحليل هذه الظاهرة تحليلا كليا أو جزئيا. فإما أن يطلق على مجموع هذه الظاهرة، وإما أن يطلق على جزء من أجزائها، وهو على كل حال ظاهرة أولية يتعذر عليك أن تظفر بها نقية خالصة مجردة من الشوائب، ولكنك تستطيع أن تتقرب منها تقربك من حد نهائي. ويمكن أن يعتبر الإحساس ظاهرة مختلطة، أي ظاهرة انفعالية وعقلية معا، فهو انفعالي، لأنه عبارة عن تبدل في نفس المدرك، وهو عقلي، لأنه يشتمل على معرفة بالشيء الخارجي، وينحصر معناه فيطلق على الناحية الانفعالية وحدها، فيصبح بهذا المعنى الأخير مقابلا للإدراك “ر: إدراك”. قال ابن سينا: «فإني إنما أعرف أن لي قلبا ودماغا بالإحساس والسماع والتجارب» “الشفاء 1 - 363”، وقال الجرجاني: «الإحساس إدراك الشيء بإحدى الحواس، فإن كان الإحساس للحس الظاهر فهو المشاهدات، وإن كان للحس الباطن فهو الوجدانيات» “التعريفات”.\nوقال التهانوي: «الإحساس هو قسم من الإدراك، وهو إدراك الشيء الموجود في المادة الحاضرة عند المدرك، مكنوفة بهيئات مخصوصة من الأين والكيف والكم والوضع وغيرها، فلا بد له من ثلاثة أشياء:\nحضور المادة، واكتناف الهيئات، وكون المدرك جزئيا. والحاصل، أن الإحساس إدراك الشيء بالحواس الظاهرة على ما تدل عليه الشروط المذكورة» “الكشاف”.\nوالاحساسات الداخلية- Sensations internes” هي الاحساسات\nالتي يعزوها المدرك الى بدنه، لا إلى شيء خارج عنه، كالجوع، والعطش، وآلام الرأس والأسنان، والصداع وغيرها.\nوالحس “ Sens” هو القوة التي بها تدرك الاحساسات، والحواس هي آلات الحس. قال ابن سينا:\n«الحس إنما يحس شيئا خارجا، ولا يحس ذاته، ولا آلته، ولا إحساسه» “الشفاء الشفاء 1 - 350، النجاة 293 - 294”. وقال أيضا:\n«الحس إنما يدرك الجزئيات الشخصية» “النجاة 110”، وقال التهانوي:\n«الحس هو القوة المدركة النفسانية» “الكشاف”، «و الحواس هي المشاعر الخمس، وهي البصر والسمع والذوق والشم واللمس» “الكشاف”.\nوالحسي أو المحسوس “ Sensible” هو ما يدرك بالحواس. قال التهانوي، «الحسي هو المنسوب الى الحس، فهو عند المتكلمين ما يدرك بالحس الظاهر، وعند الحكماء ما يدرك بالحس الظاهر أو الباطن، والحسي يسمّى محسوسا، ويقابل الحسي العقلي»، وقال أيضا: «المحسوس هو الحسي أي المدرك بالحس» “الكشاف”، وقد يطلق الحسي على الشيء المنسوب إلى الإحساس أو على الشيء المؤلف من الاحساسات، كقولنا: الأفعال أو العمليات الحسية “ Operations sensitives”، وقد يطلق أيضا على الشيء المنسوب الى أعضاء الحس، كقولنا الأعضاء الحسية “ Organes sensoriels”.\nوالمذهب الحسي “ sensualime” هو مذهب القائلين أن المعرفة لا تنشأ إلا عن الاحساس.\nوالحاسّ هو الشيء الذي يحسّ كقولنا الجهاز الحاس “ Appareil sensitif”.\nوالحساسيه أو قابلية الحس “ Sensibilite” تدل على عدة معان:\nآ- قوة الحس، وهي بهذا المعنى مقابلة لقوة العقل.\nب- قوة الشعور بالأحوال الانفعالية كاللذات والآلام والميول والهيجانات والأهواء.\nج- دقة الإحساس.\nوالحساسية العامة “ generale Sensibilité” هي الشعور بالإحساسات الداخلية، أما الحساسية الخاصة “ Sensibilité speciale” فهي الشعور بالإحساسات الظاهرة المتولدة من مؤثرات خارجة عن البدن.\n “ر: الحس، الحساسية”.",
      arabicTranslation: "sensation",
      frenchTranslation: "Sensation",
      name: "الاحساس"),
  FruitInfo(
      definition:
          "الإحسان فعل ما ينبغي أن يفعل من الخير. وهو إما أن يكون ذاتيا يبقى ولا ينقطع، ويتزايد ولا ينتقص، وإما أن يكون عرضيا ينقطع، ويلحق فيه اللوم. ومقام المحسنين عند مسكويه هو «رتبة الذين يعملون بما يعلمون» “تهذيب الاخلاق ص 132 من طبعة الجامعة الأميركية في بيروت 1966”، فإنّ المرء في نظره يتقرّب إلى اللّه تعالى بالإحسان الى نفسه والى المستحقين من أهل نوعه. فمعنى الإحسان إذن هو العمل بالفضائل.\nوقد أطلق “سبنسر” لفظ الإحسان في كتاب مبادئ الأخلاق “ Principles of Ethics, Spencer” على الواجبات والأفعال الأخلاقية التي يتخطى بها المرء حدود العدالة. كالمحبة.\nفإنه يعرض لمن كانت المحبة سيرته أن يحسن الى غيره احسانا ذاتيا من غير أن يكون ذلك الإحسان واجبا عليه في الشرع.",
      arabicTranslation: "Beneficence",
      frenchTranslation: "Bienfaisance",
      name: "الاحسان"),
  FruitInfo(
      definition:
          "الاحصاء في اللغة عد الأشياء وضبطها، وهو في الأصل علم الدولة، لاقتصاره على دراسة شؤون الدولة من جباية، وتجنيد، ودخل، وخرج.\nوقد أطلق هذا اللفظ بعد ذلك على جمع نوع معين من الوقائع والأشياء المتعلقة بسكان الدولة، من جهة ما هي قابلة للعد والقدر، ثم وسع معناه فأطلق على العلم الذي يجمع عددا كبيرا من ظواهر كل نوع من الموجودات لتنسيقها وكشف علاقاتها العددية الدالّة على أسبابها.\nوجملة القول أن علم الإحصاء يبحث في الحصول على قيم معينة تمثل الاتجاهات التي تشير إليها مجموعة كبيرة من الأرصاد، والقياسات. وأشهر مقياس احصائي هو الوسط الحسابي، وهو عبارة عن قيمة متوسطة لمجموعة من الأرصاد، وإلى جانبه مقياس آخر، وهو الانحراف القياسي، الذي يبين مدى بعد القيم الفردية عن الوسط الحسابي، وثمة مسألة أخرى، وهي مسألة العينات التي تهدف الى معرفة مدى انطباق الإحصاء الخاص بمجموعة صغيرة على مجموعة كبيرة من القيم، ولا يمكن تحديد هذا المدى الّا بحساب الاحتمالات والرياضيات العالية، وقد عمّ استعمال هذه الطريقة في ايامنا هذه حتى طبقت في البحوث العلمية، والاجتماعية، ومسائل التأمين، والمال، والتعليم وغيرها. وأهم الإحصاءات الاجتماعية إحصاءات السكان التي تبين معدلات الزواج، والطلاق، والمواليد، والوفيات، والانتحارات، واختلافها باختلاف المهن والأقاليم والشعوب والأجيال والأديان.\nو الإحصائي “ statistical” هو المنسوب إلى الاحصاء، أو المتعلق بالاحصاء، تقول: المقياس الإحصائي، والطريقة الاحصائية.\nوالنظام الإحصائي هو النظام الذي يتجلى في مجموعة كبيرة من الحوادث أو في وسطها الحسابي.\nوالحتمية الإحصائية هي الحتمية المبنية على نتائج الإحصاءات، والمثال منها ثبوت الظواهر الاجتماعية، والعلاقة المباشرة بين الأرقام المعبرة عنها. وقد بين العلماء أن لعلم الإحصاء فائدتين: أولاهما نظرية، وهي تفسير حوادث الماضي، وثانيتهما عملية، وهي التنبؤ بالمستقبل والعمل على تخطيطه.",
      arabicTranslation: "Statistics",
      frenchTranslation: "Statistique",
      name: "الاحصاء"),
  FruitInfo(
      definition:
          "الاختراع هو الإنشاء، والابتداع، كإنشاء الأفكار إنشاء جديدا، أو تنظيم وسائل العمل تنظيما جديدا، يؤدي إلى تحقيق غاية معيّنة. فالاختراع بهذا المعنى مقابل للاكتشاف “ Decouverte”، لأن الاكتشاف هو الاطلاع على الأشياء الموجودة سابقا، أي المتقدمة في الوجود على معرفتنا بها، على حين أن الاختراع هو الإيجاد، أي إيجاد أشياء جديدة لم تكن موجودة من قبل، كاختراع القصّة أو الآلة، او المركبات الكيماوية الجديدة، الخ. \n“ر: الإبداع”",
      arabicTranslation: "Invention",
      frenchTranslation: "Invention",
      name: "الاختراع"),
  FruitInfo(
      definition:
          "الاختلاف ضد الاتفاق. والفرق بينه وبين الخلاف أن الاختلاف يستعمل في القول المبني على دليل، على حين أن الخلاف لا يستعمل إلا فيما لا دليل عليه. والاختلاف عند بعض المتكلمين هو كون الموجودين غير متماثلين وغير متضادين.\nوطريقة الاختلاف في المنطق احدى طرق “ستوارت ميل”، وقاعدتها أن تقول: إذا كانت الحالتان اللتان تقع الظاهرة في إحداهما، ولا تقع في الأخرى، متفقتين في جميع الظروف الّا في ظرف واحد، فإن هذا الظرف الوحيد الذي تتّفقان فيه هو نتيجة تلك الظاهرة، أو علتها، أو الجزء الضروري من علتها.\nوطريقة الاختلاف مقابلة لطريقة الاتفاق، أي لطريقة التلازم في الوقوع، وهي أكثر خطورة منها في البرهان على صدق الفريضة، حتى لقد سماها العلماء بالطريقة الحاسمة، وقالوا: أن خير طريقة للبرهان على أن حادثة ما تلعب دور العلة في حادثة اخرى هي ان ترفع الأولى فترتفع الثانية معها. \n“ر: الطريقة، الفصل، الاتفاق”.",
      arabicTranslation: "Difference \"Method of ",
      frenchTranslation: "Différence \"Méthode de ",
      name: "الاختلاف (طريقة)"),
  FruitInfo(
      definition:
          "الاختيار ترجيح الشيء، وتخصيصه، وتقديمه على غيره، وهو أخص من الارادة، وله عند القدماء معنيان: الأول كون الفاعل بحيث إن شاء فعل، وإن لم يشأ لم يفعل، والثاني صحة الفعل والترك، بمعنى أن المختار هو القادر الذي يصح منه الفعل والترك، فإن شاء فعل، وإن شاء ترك.\nوالمقصود بحرية الاختيار القدرة على اختيار أحد المقدورين، أو اتصاف الإرادة بالقدرة على الفعل دون التقيد بأسباب خارجية. والقول بحرية الاختيار مذهب الذين يرون أن للمرء فيما يريد أو يفعل، حرية أو قدرة واستطاعة عليه. ويطلق على القائلين بحرية الاختيار اسم القدريّة، ومذهبهم مضاد لمذهب القائلين بالحتمية أو بالجبر.\n “ر:، القدرية، الحتمية الحرية، الإرادة”.",
      arabicTranslation: "Free Will",
      frenchTranslation: "Libre arbitre",
      name: "الاختيار (حرية)"),
  FruitInfo(
      definition:
          "الإخلاص في اللغة ترك الرياء في الطاعات، وفي الاصطلاح تخليص القلب من الشوائب المكدرة لصفائه، تقول: أخلص له الحب.\nوالإخلاص للدولة هو الوفاء بحقها، ومنه قولهم: المواطن المخلص.\nوالإخلاص للّه تعالى هو القيام بما يجب من حقوقه، وطريقه تطهير القلب بالكلية عما سوى اللّه.\nوقيل: الاخلاص أن لا تطلب لعملك شاهدا غير اللّه، وان تصفّي عملك من الكدورات “تعريفات الجرجاني” وقيل: ترك العمل لأجل الناس رياء، والعمل لأجلهم شرك، والإخلاص هو الخلاص من هذين.\nوالفرق بين الإخلاص والصدق أن الصدق أصل، والإخلاص فرع، وان الإخلاص لا يكون الّا بعد الشروع في العمل.\nومذهب الإخلاص “ Loyalisme” مذهب أخلاقي قوامه الصدق، والصراحة، والبعد عن الغش والاحتيال والرياء.",
      arabicTranslation: "Loyalty",
      frenchTranslation: "Loyauté",
      name: "الاخلاص"),
  FruitInfo(
      definition:
          "الأخلاق في اللغة جمع خلق، وهو العادة، والسجيّة، والطبع، والمروءة، والدين. وعند القدماء ملكة تصدر بها الأفعال عن النفس من غير تقدّم روية وفكر وتكلف.\nفغير الراسخ من صفات النفس لا يكون خلقا، كغضب الحكيم، وكذلك الراسخ الذي تصدر عنه الأفعال بعسر وتأمل، كالبخيل إذا حاول الكرم.\nوقد يطلق لفظ الأخلاق على جميع الأفعال الصادرة عن النفس محمودة كانت أو مذمومة، فتقول فلان كريم الأخلاق، أو سيّئ الأخلاق. وإذا اطلق على الأفعال المحمودة فقط دلّ على الأدب، لأن الأدب لا يطلق الّا على المحمود من الخصال. فإذا قلت:\nأدب القاضي أردت به ما ينبغي للقاضي أن يفعله، وكذلك إذا قلت:\nآداب الوزراء، والكتاب، والمعلمين، والمتعلمين. وفي كتابي الأدب الكبير والأدب الصغير لابن المقفع، وكتاب أدب الدنيا والدين للماوردي أمثلة كثيرة تفسر هذا المعنى.\nوالفرق بين الأدب والتعليم أن الأدب يتعلق بالعادات، والتعليم بالشرعيات، الأول عرفي دنيوي، والثاني شرعي ديني. وقد يطلق الأدب على السنّة أو على الورع وصيانة النفس. وله عند العرب عدة مصادر، وهي الشعر الجاهلي، والقرآن، والحديث، والسير، وهو متقدم على علم الاخلاق المشتمل على الكثير من العناصر اليونانية والفارسية والهندية.\nويسمى علم الأخلاق “ La Morale” بعلم السلوك، أو تهذيب الأخلاق، أو فلسفة الأخلاق “ Éthique”، أو الحكمة العملية، أو الحكمة الخلقية. والمقصود به معرفة الفضائل، وكيفية اقتنائها، لتزكو بها النفس، ومعرفة الرذائل لتتنزه عنها النفس “ر: كتاب تهذيب الأخلاق لمسكويه”. ولمعرفة ما يجب على الإنسان فعله لبلوغ السعادة تكلم الفلاسفة على طبيعة الوجدان، والضمير، وطبيعة الخير والعدل والواجب والمحبّة، وبنوا جميع المفاهيم الخلقية التي تصوروها على الأسس المستمدة من مبادئهم الفلسفية العامة. ونحن نطلق اليوم لفظ الأخلاق على المعاني التالية:\n1 - الأخلاق النسبية وهي مجموع قواعد السلوك المقررة في زمان معين لمجتمع معين. تقول: اخلاق العرب، واخلاق الفرس، واخلاق الروم. فلكل شعب أخلاقه المتفقة مع شروط وجوده، ولا يمكنك أن تحمله على أخلاق غير أخلاقه دون تعريض نظام حياته للاضطراب والفساد Durkheim, division du travail(social ch II . p 262\n2 - الأخلاق المطلقة، وهي مجموع قواعد السلوك الثابتة، التي تصلح لكل زمان ومكان. ويسمّى العلم الذي يبحث في هذه الاخلاق بفلسفة الأخلاق، وهي الحكمة العملية التي تفسر معنى الخير والشر، وتنقسم الى قسمين: أحدهما عام مشتمل على مبادئ السلوك الكلية، والآخر خاص مشتمل على تطبيق هذه المبادئ في مختلف نواحي الحياة الإنسانية. وجماع ذلك كله تحديد ما يجب أن يكون، لا وصف ما هو كائن في الواقع.\n3 - الأخلاق النهائية والأخلاق المؤقتة: لقد فرّق “ديكارت” في كتابه “مقالة الطريقة” بين الأخلاق النظرية أو النهائية المبنية على المبادئ الفلسفية، وبين الأخلاق المؤقتة “ Provisoire” المشتملة على بعض القواعد العملية التي تصلح للحياة في مجتمع معين. وقريب من ذلك أيضا قول “ليفي بروهل” ان التقدم الاخلاقي لا يدل على تقدم النظريات الاخلاقية، بل يدل على مطابقة السلوك العملي لقواعد الأخلاق في حياة إنسانية أفضل.\n4 - وأخلاق المواقف “ Morale de situation ” هي الأخلاق المبنية على تحديد المعطيات المعقدة الخاصة بكل حالة من حالات الحياة، لا الأخلاق المستنبطة من القوانين العامة.\n5 - والاخلاق الساكنة “ statique” او المغلقة “ close” عند “هنري برغسون” مقابلة للأخلاق الحراكية “ Dynamique” أو المتفتحة “ ouverte”. Les deux sources, H.Bergson” 286 P والى جانب لفظ الأخلاق ثلاثة ألفاظ أخرى لا بد من الاشارة إليها هنا، وهي:\n1 - الأخلاقي “ Moral” وهو المنسوب إلى الأخلاق أو إلى قواعد السلوك المقررة في زمان معين، مثال ذلك قول “دوركهايم”: الحادث الأخلاقي لا يكون سويا في مجتمع معين الّا اذا كان شائعا في العدد المتوسط من المجتمعات الأخرى التي هي من نوع ذلك المجتمع. تقول بهذا المعنى:\nالحقيقة الأخلاقية، والواقع الأخلاقي “ Réalité Morale” والحس الأخلاقي. “ Sens moral”\nوالأخلاقي أيضا هو المتعلق بالحكمة الخلقية.\nوالأخلاقي اخيرا مقابل للّاأخلاقي “ Immoral”، ويطلق على الأفعال الحميدة المطابقة للاخلاق او لقواعد السلوك العملية. ويطلق الأخلاقي “ Moral” في اللغة الفرنسية على العملي “ Pratique” أو على المعنوي وهو المتعلق بالنفس لا بالبدن.\nتقول: الثروة الأخلاقية، والعلوم الأخلاقية أي المعنوية.\nوفرقوا بين الأمر الأخلاقي، والأمر الذي هو بمعزل عن الأخلاقي “ A Moral”، كسلوك الحيوان، فهو سلوك محايد لا يوصف بالأخلاقي ولا باللّاأخلاقي، لأن هاتين الصفتين تقتضيان تصوّر الفعل والقصد إليه، أو البعد عنه، وليس ذلك شأن الحيوان.\n-فائدة- إذا أضفت لفظ الأخلاق إلى لفظ آخر، دل على مجموع قواعد السلوك المتعلقة بالشيء الذي يدل عليه ذلك اللفظ، تقول: أخلاق المنفعة، واخلاق اللذة، واخلاق الواجب، وكذلك إذا نسبته إلى جماعة معينة، دلّ على قواعد السلوك الخاصة بتلك الجماعة، تقول: الأخلاق المهنية، والأخلاق المسيحية، والأخلاق الرواقية والأخلاق الاشتراكية.\n2 - المذهبية الأخلاقية “ Moralisme “ ، هي النظرية التي تقرر أن للأخلاق قيمة مطلقة. مثال ذلك: أن مبدأ الفلسفة الأعلى عند “فيخته” هو قانون العمل، لا قانون الوجود، فإذا صح هذا القول، لزم عنه عدة نتائج، وهي:\nا- أن لمبادئ الأخلاق قيمة مطلقة، وهي الأصل الذي ترجع إليه جميع القيم الانسانية.\nب- أن علم الأخلاق مستقل عن علم ما بعد الطبيعة، على حين ان علم ما بعد الطبيعة تابع لعلم الأخلاق.\nج- أنّ ما يجب على الإنسان لنفسه والأبناء جنسه متقدم على ما يجب عليه لخالقه.\nد- أن بحث المرء عن خيره الذاتي متقدم على بحثه عن الخير الموضوعي.\nوكثيرا ما تؤدي المبالغة في المذهبية الأخلاقية الى التشدد والتعصب على النحو الذي نجده عند زمتاء المعلمين.\nوالمذهبية الأخلاقية ضد المذهبية اللاأخلاقية التي تنكر قيم الأخلاق، أو تغير ترتيبها الموضوعي، والمثال من هذه المذهبية اللاأخلاقية مذهب “نيتشه”، فإن هذا المذهب لا ينكر جميع قيم الأخلاق، بل يستبدل بالأخلاق المسيحية القائمة على المحبة قيما اخلاقية جديدة تقوم على إرادة القوة وعبادة الإنسان الأعلى “ Surhomme” الذي يضرب بأخلاق المحبة عرض الحائط لأنها أخلاق الضعفاء.\n3 - وتطلق الأخلاقية “ Moralité” من جهة ما هي صفة، على الأمر الذي يتضمن معنى الخير والشر، بخلاف الأمر الذي هو بمعزل عن الأخلاق.\nوهي إيجابية أو سلبية، فالإيجابية تتعلق بالأفعال الحميدة، والسلبية تتعلق بالأفعال المذمومة. وإذا أطلقت لفظ الاخلاقية على مبادئ السلوك دلّ على القيم المطابقة للمثل الأعلى الأخلاقي.\nوإذا أطلقته على السلوك العملي دلّ على مطابقة هذا السلوك لمبادئ الأخلاق.\n “ر: مقالتنا في الأخلاق، دائرة المعارف، المجلد 7، بيروت 7691”.",
